CDCR.tm_menubar_onmouseout = function()
{
    var menubar = document.getElementById('cdcr_testmode_overlay');
    var container = document.getElementById('cdcr_testmode_overlay_container');
    
    menubar.style.opacity = 0.4;
    menubar.style.height = '0px';
    menubar.style.overflow = 'hidden';
    container.style.height = '0px';
    container.style.overflow = 'hidden';
}

CDCR.tm_menubar_onmouseover = function()
{
    var menubar = document.getElementById('cdcr_testmode_overlay');
    var container = document.getElementById('cdcr_testmode_overlay_container');
    
    menubar.style.opacity = 1;
    menubar.style.height = 'auto';
    menubar.style.overflow = 'visible';
    container.style.height = 'auto';
