if (typeof reemplazarGuion == 'undefined') {
    function reemplazarGuion(cadena) {
        var re, r, result;
        re = /_/i;
        r = cadena.replace(re," ");
        re = /Espana/i;
        result = r.replace(re,"Espa\xf1a");
        return(result);
    }
}

function mf_fix_changeStateOptions(state_fieldname) {
    var p;
    var p2;

    var formu = CDCR.__getformu();
    var state = document.getElementById(state_fieldname);

    p = CDCR.__getPreparedValue('Country');
    p2 = reemplazarGuion(CDCR.__getPreparedValue('Country'));
    formu.value = p2;

    try {
        seleccion=eval("pais_"+p);
        seleccionNames=eval("paisstnames_"+p);
        cuantos_add=seleccion.length;

        if (typeof state!= "undefined"){
            state.length=cuantos_add;
            for(i=0;i<cuantos_add;i++){
                state.options[i].value=seleccion[i];
                state.options[i].text=seleccionNames[i];
                if (seleccion[i]=="") {
                    state.options[i].selected = true;
                }
            }
        }
    } catch(e){
        if (typeof state!= "undefined"){
            state.length=1;
            state.options[0].value=353;
            state.options[0].text='Otra';
            state.options[0].selected = true;
        }
    }

    mf_fix_state_loaded_value = CDCR.__getPreparedValue('Country');
    cdcr_mapped_field_onchange('State','');
}

