var cdcr_position_count = Array();
var cdcr_position_visible_count = Array();
function cdcr_position_count_add(divname, targetdiv) {
    var parentid;
    //alert(divname);
    if (typeof(cdcr_position_count[targetdiv])!='undefined') {
        parentid = document.getElementById(divname).parentNode.id;
        //alert('PARENT-ID'+document.getElementById(divname).parentNode.id);
        if (typeof(cdcr_position_count[parentid])!='undefined') {
            cdcr_position_count[parentid]--;
        }
        cdcr_position_count[targetdiv]++;
    } else {
        cdcr_position_count[targetdiv]=1;
    }
}

function cdcr_position_visible_count_add(divname, targetdiv) {
    var parentid;
    //alert(divname);
    if (typeof(cdcr_position_visible_count[targetdiv])!='undefined') {
        parentid = document.getElementById(divname).parentNode.id;
        //alert('PARENT-ID'+document.getElementById(divname).parentNode.id);
        if (typeof(cdcr_position_visible_count[parentid])!='undefined') {
            cdcr_position_visible_count[parentid]--;
        }
        cdcr_position_visible_count[targetdiv]++;
    } else {
        cdcr_position_visible_count[targetdiv]=1;
    }
}

function cdcr_position_visible_count_hide_empty() {
    for (var targetdiv in cdcr_position_visible_count) {
        if (cdcr_position_visible_count[targetdiv]==0) {
            if (document.getElementById(targetdiv)!=null) {
                document.getElementById(targetdiv).style.display = 'none';
            }
        }
    }
}

function cdcr_position_campaign_ByOrder(idorder, divname, display) {
     
    var cdcr_targetdiv = null;

    if (typeof(cdcr_position_byOrder)!='undefined') {
        
        if (typeof(cdcr_position_byOrder['default'])!='undefined') {
            cdcr_targetdiv = cdcr_position_byOrder['default'];
        }

        if (typeof(cdcr_position_byOrder[idorder])!='undefined') {
            cdcr_targetdiv = cdcr_position_byOrder[idorder];
        }

        if ((cdcr_targetdiv!=null)&&(document.getElementById(cdcr_targetdiv)!=null)) {
            cdcr_position_count_add(divname, cdcr_targetdiv);
            if (display=='block') {
                cdcr_position_visible_count_add(divname, cdcr_targetdiv);
                document.getElementById(cdcr_targetdiv).style.display='block';
            }
            document.getElementById(cdcr_targetdiv).appendChild(
                document.getElementById(divname));
        }
    }
    
    document.getElementById(divname).style.display=display;
     
}

