CDCR.__fix_inline_popups_dom = function() {

    var popup; var vpage;
    var pagebody = document.getElementsByTagName('body')[0];
    
    if (pagebody) {    
        
        popup = document.getElementById('cdcr_inline_popup');
        if (popup) {
            pagebody.appendChild(popup);
        }
    
        if (CDCR.campaigns['standard']) {

            var idcampana;
            for (idx=0;idx<CDCR.campaigns['standard'].length;idx++) {
                idcampana = CDCR.campaigns['standard'][idx];
                
                popup = document.getElementById('cdcr_inline_popup_'+idcampana);
                if (popup) {
                    pagebody.appendChild(popup);
                }
                
                vpage = document.getElementById('cdcr_inline_vpage_'+idcampana);
                if (vpage) {
                    pagebody.appendChild(vpage);
                    vpage.style.display = 'block';
                    pagebody.style.overflow = 'hidden';
                }
                
                
            }

        }
        
    }
    
}

