        mapped_fieldname = corrmappings[idx][3];
        if (mapped_fieldname==fieldname) {
            idcampana   = corrmappings[idx][0];
            idmensaje   = corrmappings[idx][1];
            
            if (document.getElementsByName(fieldname+'_mapped_'+idcampana+'_'+idmensaje)[0]!=null)
                document.getElementsByName(fieldname+'_mapped_'+idcampana+'_'+idmensaje)[0].value = CDCR.data[fieldname].value;

            if (document.getElementsByName(fieldname+'_extra_'+idcampana+'_'+idmensaje)[0]!=null)
                document.getElementsByName(fieldname+'_extra_'+idcampana+'_'+idmensaje)[0].value = CDCR.data[fieldname].value;
            
