function __cdcr_replace_crptags(crpvalues, urltomap) {
    
    var crp; var crpvalue;
    for (crp in crpvalues) {
        crpvalue = crpvalues[crp];
        urltomap = urltomap.replace(crp,encodeURIComponent(crpvalue));
    }

    if (typeof(cdcr_idweb)!= 'undefined'){  urltomap = urltomap.replace("[crp_idweb]",encodeURIComponent(cdcr_idweb));  }  ;    
    if (typeof(cdcr_nameweb)!= 'undefined'){  urltomap = urltomap.replace("[crp_nameweb]",encodeURIComponent(cdcr_nameweb));  }  ;   
    if (typeof(cdcr_subid)!= 'undefined'){  urltomap = urltomap.replace("[crp_subid]",encodeURIComponent(cdcr_subid));  }  ;  
    if (typeof(cdcr_subadv)!= 'undefined'){  urltomap = urltomap.replace("[crp_subadv]",encodeURIComponent(cdcr_subadv));  }  ;       
    
    urltomap = __cdcr_replace_special_tags(urltomap);
            
  return urltomap;         
} 

function __cdcr_replace_special_tags(url) {
    var res = url.split("[crp_timestamp]");
    if (res.length > 2) {
        for (var i = 0; i < res.length; i++)
        {
            if ((i % 2 == 0)) {
            } else {
                date = res[i];
                date = date.split("/");
                var timestamp = new Date(date[2], date[0], date[1]);
                cdcr_timestamp = timestamp.getTime();
                cdcr_timestamp = cdcr_timestamp.toString();
                cdcr_timestamp = cdcr_timestamp.substr(0, (cdcr_timestamp.length - 3));
                url = url.replace("[crp_timestamp]" + res[i] + "[crp_timestamp]", encodeURIComponent(cdcr_timestamp));
            }
        }
    }
    return url;
}

