var cdcr_provider_valid_email_retries = new Array();
var cdcr_provider_valid_email = new Array();

function regexp_email(value) {
    var regexp_email = /^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*(\.[a-z]{2,4})$/i
    return regexp_email.test(value);
}

function __cdcr_email_set_failedvalidator(email) {
    if (cdcr_provider_valid_email_retries[email]==undefined) {
        cdcr_provider_valid_email_retries[email] = 1;
    } else {
        cdcr_provider_valid_email_retries[email]++;
    }
}

function __cdcr_email_validator_provider(email, idweb, idprov, originator) {
    
    var email_ok = regexp_email(email);
    if (!email_ok) return false;
    if (idprov<=0) return true;
    
    if (cdcr_provider_valid_email_retries[email]!=undefined) {
        if (cdcr_provider_valid_email_retries[email]>=3) return true;
    }    
    
    if (cdcr_provider_valid_email[idprov]!= undefined ) {
        if (cdcr_provider_valid_email[idprov][email]!= undefined ) {
            return cdcr_provider_valid_email[idprov][email];
        }
    }
    
    cdajx_call(
        'http://registro.correodirect.com/fvalidator.php?idweb='+idweb+'&idprov='+idprov+'&value='+email+'&o='+originator,
        function(response) {
           if (response.email.validated==1) {
              if (response.email.validation==1) {email_ok = true;} else { email_ok = false; }
              if (cdcr_provider_valid_email[idprov]== undefined) { cdcr_provider_valid_email[idprov] = new Array(); }
              cdcr_provider_valid_email[idprov][email] = email_ok;
           }
        },
        function(response) { 
            console.log('error:'+response);
        },
        {async:false, dataType:'json'}
    );
    return email_ok;
} 
