CDCR.tm_menubar_exit = function()
{
   var menubar = document.getElementById('cdcr_testmode_overlay');
   menubar.onmouseout = null;
   
   CDCR.tm_menu_open('exit');
}

CDCR.tm_menubar_onmouseout = function()
{
    var menubar = document.getElementById('cdcr_testmode_overlay');
    var container = document.getElementById('cdcr_testmode_overlay_container');
    
    menubar.style.opacity = 0.4;
    menubar.style.height = '0px';
    menubar.style.overflow = 'hidden';
    container.style.height = '0px';
    container.style.overflow = 'hidden';
}

CDCR.tm_menubar_onmouseover = function()
{
    var menubar = document.getElementById('cdcr_testmode_overlay');
    var container = document.getElementById('cdcr_testmode_overlay_container');
    
    menubar.style.opacity = 1;
    menubar.style.height = 'auto';
    menubar.style.overflow = 'visible';
    container.style.height = 'auto';
    container.style.overflow = 'visible';
}

CDCR.tm_menu_open = function(tm_menu_id)
{
    var menu_items_container = document.getElementById('tm_menu_items_container_'+tm_menu_id);
    if (menu_items_container.style.visibility!='visible') {
        menu_items_container.style.visibility = 'visible';
        menu_items_container.style.height = 'auto';
    }
}

CDCR.tm_menu_close = function(tm_menu_id)
{
    var menu_items_container = document.getElementById('tm_menu_items_container_'+tm_menu_id);
    if (menu_items_container.style.visibility!='hidden') {
        menu_items_container.style.visibility = 'hidden';
        menu_items_container.style.height = 0;
    }
}

