CDCR.__transferSponsorchecks = function(idcampana) {
    if (document.getElementById('sporsorscheck-'+idcampana).checked) {
        CDCR.__setCampaignTerms(idcampana, false, 'infocomercial'); // los sponsorchecks son inversos a infocomercial
    } else {
        CDCR.__setCampaignTerms(idcampana, true, 'infocomercial');  // los sponsorchecks son inversos a infocomercial
    }
}

CDCR.__transferTermsToSponsorchecks = function(idcampana) {
    var accepted = __cdcr_campaign_check_accepted(idcampana);
    CDCR.__setCampaignTerms(idcampana, !accepted, 'sporsorscheck'); // los sponsorchecks son inversos a infocomercial
}

CDCR.__cascadeterms_sponsors = function (accepted) {
    CDCR.__cascadeterms(accepted, 'infocomercial', 'sponsors');
    CDCR.__cascadeterms(!accepted, 'sporsorscheck', 'sponsors'); // los sponsorchecks son inversos a infocomercial
    return true;
}

CDCR.__cascadeterms = function(accepted, termsprefix, campaigntype) {
    
    for (idx=0;idx<CDCR.campaigns[campaigntype].length;idx++) {
        
        var idcampana = CDCR.campaigns[campaigntype][idx];
        CDCR.__setCampaignTerms(idcampana, accepted, termsprefix);
        
    }
    return true;
}

CDCR.__getCascadeTermsCheckbox = function() {
    if (document.getElementById('infocomercialcascada')!=null) return document.getElementById('infocomercialcascada');
    if (document.getElementById('infocomercialcascadasi')!=null) return document.getElementById('infocomercialcascadasi');
