}

CDCR.__focusCascadeTerms = function() {
    var cascadecheckbox = CDCR.__getCascadeTermsCheckbox();
    if (cascadecheckbox!=null) {
        cascadecheckbox.focus();
    }
    return true;
}

CDCR.__setCampaignTerms = function (idcampana, accepted, termsprefix) {
    
    var checkbox = document.getElementById(termsprefix+'-'+idcampana);
    if (checkbox!=null) {
        if (accepted) { checkbox.checked = true; } else { checkbox.checked = false; }
    }

    var radiosi = document.getElementById(termsprefix+'si-'+idcampana);
    var radiono = document.getElementById(termsprefix+'no-'+idcampana);
    if ((radiosi!=null)&&(radiono!=null)) {
        if (accepted) {
            radiosi.checked = true;
            radiono.checked = false;
        } else {
            radiosi.checked = false;
            radiono.checked = true;
        }
    }
    
}

function cascadeterms_checkbox()
{
    
    var checkboxcascada = document.getElementById('infocomercialcascada');
  
    var node_list = document.getElementsByTagName('input');
    for (var i = 0; i < node_list.length; i++) {
        var node = node_list[i];

        if (node.getAttribute('type') == 'radio' || node.getAttribute('type') == 'checkbox') {         
            var str = node.name;
            var info= str.substr(0,14);
            var strid = node.id;
            var infoid= strid.substr(0,14);
            //alert(info);
         
            if (checkboxcascada.checked) {
                if (info == 'infocomercial-' && infoid =='infocomercial-') {
                    node.checked = true;
                }
                if (infoid == 'infocomercials') {
                    node.checked = true;
                }
                if (infoid == 'infocomercialn') {
                    node.checked = false;
                }
             
                if (info == 'sporsorscheck-' && infoid =='sporsorscheck-') {
                    node.checked = true;
                }
                if (infoid == 'sporsorschecks') {
                    node.checked = true;
                }
                if (infoid == 'sporsorscheckn') {
                    node.checked = false;
                }
            } else {
                if (info == 'infocomercial-') {
                    node.checked = false;
                }
                if (info == 'infocomercials') {
                    node.checked = false;
                }
                if (info == 'infocomercialn') {
                    node.checked = false;
                }
             
                if (info == 'sporsorscheck-') {
                    node.checked = false;
                }
                if (info == 'sporsorschecks') {
                    node.checked = false;
                }
                if (info == 'sporsorscheckn') {
                    node.checked = false;
                }
            }
      
        }
    }
    
}

