CDCR.__getPreparedValueForMapped = function(fieldName, idcampana, idmensaje) {
    
    if (CDCR.data[fieldName]==undefined) return '';
    
    var mf_fix_fieldname    = fieldName + '_extra_'+idcampana+'_'+idmensaje;
    var mf_mapped_fieldname = fieldName + '_mapped_'+idcampana+'_'+idmensaje;
    
    var mf_linked_field = document.getElementById(mf_fix_fieldname);
    
    if (mf_linked_field==null) {
        mf_linked_field = document.getElementsByName(mf_mapped_fieldname)[0];
    }
    
    if (mf_linked_field==null) {
        mf_linked_field = CDCR.data[fieldName];
    }
    
    return CDCR.__getPreparedValue(fieldName, mf_linked_field.value);
}

