}

var mf_fix_extra_fields = new Array();
//alert(1);

function mf_addFixExtraField(idCampana, idMensaje, mf_label, mf_fieldname, idvalidator)
{
    var genField = mf_genFixExtraField(idCampana, idMensaje, mf_label, mf_fieldname, idvalidator);
    if (genField!=null) {
        mf_addFixExtraFieldWrite(idCampana, idMensaje, genField.textNode, genField.el);
    }
    mf_dataFixExtraField(idCampana, idMensaje, mf_fieldname);
}

function mf_dataFixExtraField(idCampana, idMensaje, mf_fieldname) {
    
    var formu = CDCR.__getformu();
    
    if (typeof formu.elements[mf_fieldname] == 'undefined') {
        
        var elorig = document.createElement('input');
        
        elorig.type = 'hidden';
        elorig.name = mf_fieldname;
        elorig.id   = mf_fieldname;

        formu.appendChild(elorig);

        if (typeof CDCR.data == 'undefined') CDCR.data = new Array();
        CDCR.data[mf_fieldname] = elorig;

    }
    
}

function mf_genFixExtraField(idCampana, idMensaje, mf_label, mf_fieldname, idvalidator)
{
    //if (mf_fieldname=='BirthYear' || mf_fieldname=='BirthMonth' || mf_fieldname=='BirthDay')
    //   alert('mf_addFixExtraField'+mf_fieldname);
    var mf_fix_fieldname    = mf_fieldname + '_extra_'+idCampana+'_'+idMensaje;
    var mf_mapped_fieldname = mf_fieldname + '_mapped_'+idCampana+'_'+idMensaje;

    if (typeof mf_fix_extra_fields[mf_fieldname] == 'undefined') {
        mf_fix_extra_fields[mf_fieldname] = new Array();
    }
    mf_fix_extra_fields[mf_fieldname].push(new Array(idCampana,idMensaje));
   
    if (document.getElementsByName(mf_mapped_fieldname)[0] == null) {
        
        var textNode = document.createTextNode(mf_label);
        var mf_type;
        if (mf_fieldname in {
            'Country':'',
            'State':'',
            'Sex':'',
            'BirthYear':'',
            'Profession':'',
            'Education':'',
            'Industry':'',
            'BirthMonth':'',
            'BirthDay':'',
            'Province':''
        }) {
            mf_type = "select";
        } else {
            mf_type = "text";
        }
        //alert(mf_fieldname+' is '+ mf_type);

        // Create field
        switch (mf_type) {
            case 'text':
                var el = document.createElement('input');
                el.setAttribute('fieldname',mf_fieldname);
                el.type = mf_type;
                el.name = mf_fix_fieldname;
                el.id   = mf_fix_fieldname;
                el.setAttribute("OnChange","cdcr_mapped_field_onchange('"+mf_fieldname+"',this.value)");
                break;
            case 'select':
                var el = document.createElement('select');
                el.setAttribute('fieldname',mf_fieldname);
                //el.type = mf_type;
                el.name = mf_fix_fieldname;
                el.id   = mf_fix_fieldname;
                el.setAttribute("OnChange","cdcr_mapped_field_onchange('"+mf_fieldname+"',this.value)");

                switch (mf_fieldname) {
                    case 'BirthYear':
                        mf_fix_addBirthYearOptions(el);
                        break;
                    case 'Country':
                        mf_fix_addCountryOptions(el);
                        break;
                    case 'State':
                        mf_fix_addStateOptions(el)
                        el.setAttribute("OnFocus","mf_fix_sync_State()");
                        break;
                    case 'Sex':
                        mf_fix_addGenderOptions(el, idvalidator);
                        break;
                    case 'Profession':
                        mf_fix_addProfessionOptions(el)
                        break;
                    case 'Education':
                        mf_fix_addEducationOptions(el)
                        break;
                    case 'Industry':
                        mf_fix_addIndustryOptions(el)
                        break;
                    case 'BirthMonth':
                        mf_fix_addBirthMonthOptions(el)
                        break;
                    case 'BirthDay':
                        mf_fix_addBirthDayOptions(el)
                        break;
                    case 'Province':
                        mf_fix_addProvinceOptions(el, idvalidator)
                        break;
                }

                break;
        }

        // Apply styles
        switch (mf_type) {
            case 'text':
                el.size = 20;
                //el.setAttribute("style","width:229px; height: 24px; margin-left: 0pt; margin-right: 0pt; padding-left: 8px; padding-top: 1px;");
                break;
            case 'select':
                //el.setAttribute("style","width:229px; margin-left: 0pt; margin-right: 0pt; padding-left: 8px; padding-top: 1px;");
                break;
        }
        // if ((mf_fieldname=='BirthYear' || mf_fieldname=='BirthMonth' || mf_fieldname=='BirthDay')) el.disabled= !disabled_value;
        //if ((mf_fieldname=='BirthYear' || mf_fieldname=='BirthMonth' || mf_fieldname=='BirthDay'))
        //    el.setAttribute("style","width: auto;");

        var genField = new Object();
        genField.el = el;
        genField.textNode = textNode;
        
        return genField;
    }

    return null;

}

function __cdcr_css_add_class(id, newclass) {
    var el = document.getElementById(id);
    var oldclass = el.getAttribute('class');
    oldclass = oldclass+' '+newclass;
    oldclass = oldclass.replace('  ',' ');
    el.setAttribute('class', oldclass);
}

function __cdcr_css_remove_class(id, removclass) {
    var el = document.getElementById(id);
    var oldclass = el.getAttribute('class');
    oldclass = oldclass.replace(removclass,'');
    el.setAttribute('class', oldclass);
}

function mf_addFixExtraFieldWrite(idCampana, idMensaje, textNode, el){

    var tblid;
    tblid = 'div_xtra_fields_'+idCampana+'_'+idMensaje;
    var tbl = document.getElementById(tblid);
    var row;
    var cellLeft;
    var cellRight;
    
    row = document.createElement('div');
    row.setAttribute('id','div_'+el.getAttribute('fieldname')+'_extra_'+idCampana+'_'+idMensaje);
    row.setAttribute('class','cdcr_form_fieldrow '+el.getAttribute('fieldname'));
    row.setAttribute('style','overflow:hidden;');

    cellLeft = document.createElement('div');
    cellLeft.setAttribute('class','cdcr_field_label');
    cellLeft.appendChild(textNode);
    cellRight = document.createElement('div');
    cellRight.setAttribute('style','float:left;width:100%;');
    cellRight.appendChild(el);

    row.appendChild(cellLeft);
    row.appendChild(cellRight);

