function cdcr_getAffiliateId()
{
    if (typeof(cdcr_idweb)!='undefined') return cdcr_idweb;
    return CDCR.impl.Config['Affiliate'];
}

CDCR.__prepareUrlData = function() {
    var urlData = '?Affiliate='+cdcr_getAffiliateId();
    if (typeof CDCR.data == 'undefined') return urlData;
    var fieldvalue;
    for(var fieldname in CDCR.data) {
        if (typeof(CDCR.data[fieldname])!= 'undefined') { fieldvalue = CDCR.__getPreparedValue(fieldname); } else { fieldvalue = ''; }
        urlData = urlData + '&' + fieldname + '=' + fieldvalue;
    }
    subidvalue = __cdcr_get_subid(); 
    if( subidvalue != '') { urlData = urlData + subidvalue; };
    
    subadvvalue = __cdcr_get_subadv(); 
    if( subadvvalue != '') { urlData = urlData + subadvvalue; };
    
    return urlData;
}

