function mf_checkYMD_bycampaign(idcampana, idmensaje)
{
    var checkYMD = true;
    
    for (var idx=0; idx<corrmappings.length;idx++) {
        mf_idcampana   = corrmappings[idx][0];
        mf_idmensaje   = corrmappings[idx][1];
        if ((mf_idcampana==idcampana)&&(mf_idmensaje==idmensaje)) {
            mapped_fieldname = corrmappings[idx][3];
            if ((mapped_fieldname=='BirthYear')||(mapped_fieldname=='BirthMonth')||(mapped_fieldname=='BirthDay')) {
                if (CDCR.data[mapped_fieldname].value=='') {
                    checkYMD = false;
                    break;
                }
            }
        }
    }
    return checkYMD;
}

function mf_fix_hidde_field(mf_fieldname) {
    var mapped_fieldname;
    var div_fieldname;
    var div_field;
    var checkHidde;
    
    for (var idx=0; idx<corrmappings.length;idx++) {
        mapped_fieldname = corrmappings[idx][3];
        
        if (mf_fieldname==mapped_fieldname) {

            idcampana   = corrmappings[idx][0];
            idmensaje   = corrmappings[idx][1];

            checkHidde = true;
            
            if ((mf_fieldname=='BirthYear')||(mf_fieldname=='BirthMonth')||(mf_fieldname=='BirthDay')) {
                checkHidde = mf_checkYMD_bycampaign(idcampana, idmensaje);
            }
            
            if (checkHidde) {
               var fieldok = true;
               mapped_fieldname = corrmappings[idx][3];
               var fieldvalidator = '__cdcr_campaign_fieldvalidator_'+idcampana;
               if (typeof(window[fieldvalidator])=='function') {
                 fieldok = window[fieldvalidator](mapped_fieldname, null);
               }
               if (!fieldok) { checkHidde = false; }
            }
            
            if (checkHidde) {
                div_fieldname    = 'div_'+mf_fieldname + '_extra_'+idcampana+'_'+idmensaje;
                var div_field = document.getElementById(div_fieldname);
                if (div_field!=null) {
                    div_field.style.display = 'none';
                }
            }
            
        }
    } 
     
}

var mf_fix_state_loaded_value = -1;

function mf_fix_sync_mapped (){
    var onchange;
    if (typeof CDCR.impl.Mapping != 'undefined') {
        for (name in CDCR.impl.Mapping){
            var assFieldName = CDCR.impl.Mapping[name];
            var formu = CDCR.__getformu();

            if (typeof formu[assFieldName] != 'undefined') {

                //alert(name + '-->' + CDCR.impl.Mapping[name]+'---' + CDCR.data[name].value +'--' +formu[assFieldName].value);
                if(formu[assFieldName].length == null ){
                    onchange = formu[assFieldName].getAttribute("onchange");
                    if(typeof(onchange) != "function") {
                        formu[assFieldName].setAttribute('onchange','cdcr_mapped_field_onchange("' + name +'",this.value)');
                    } else {
                        formu[assFieldName].onchange = function() {
                            onchange();
                            cdcr_mapped_field_onchange( name,formu[assFieldName].value);
                        };
                    }
                }else{
                    var ilength;
                    for(ilength = 0; ilength < formu[assFieldName].length; ilength++){
                        onchange = formu[assFieldName][ilength].getAttribute("onchange");
                        if(typeof(onchange) != "function") {
                            formu[assFieldName][ilength].setAttribute('onchange','cdcr_mapped_field_onchange("' + name +'",this.value)');
                        } else {
                            formu[assFieldName][ilength].onchange = function() {
                                onchange();
                                cdcr_mapped_field_onchange( name,formu[assFieldName][ilength].value);
                            };
                        }
                    }
                }
            }
        }
    }
}

