CDCR.__document_write = function (str, jstagid) {
    
    var jstag = document.getElementById(jstagid);
    
    if (jstag) {
        var cdcr_holder = document.createElement('div');
        cdcr_holder.innerHTML = str;
        jstag.parentNode.insertBefore(cdcr_holder, jstag.nextSibling);
    } else {
        document.write(str);
    }
    return true;
}

