var CDAJX_STATE_UNINITIALIZED=0; 
var CDAJX_STATE_LOADING=1; 
var CDAJX_STATE_LOADED=2;
var CDAJX_STATE_INTERACTIVE=3; 
var CDAJX_STATE_COMPLETE=4;
 
function cdajx_call(url, onSuccessHandler, onFailureHandler, options) {
  // options array(method, async, dataType)
  if (options==undefined) { options = {}; }
  
  var cdajx_request = _cdajx_create_xhr();
  if(cdajx_request) {
    cdajx_request.onreadystatechange = function() {
        if (cdajx_request.readyState == CDAJX_STATE_COMPLETE) {
            if(cdajx_request.status == 200) {
                var response = cdajx_request.responseText;
                if (options.dataType=='json') { response = eval('('+response+')'); }
                onSuccessHandler(response);
            } else {
                onFailureHandler(cdajx_request);
            }
        }
    }
    
    var method = 'GET'; if (options.method!=undefined) { method = options.method; }
    var async  = true;  if (options.async!=undefined) { async = options.async; }
        
    cdajx_request.open(method, url, async);
    cdajx_request.send(null);
  }
}
 
function _cdajx_create_xhr() {
  if(window.XMLHttpRequest) { return new XMLHttpRequest(); }
  else if(window.ActiveXObject) { return new ActiveXObject("Microsoft.XMLHTTP"); }
}
   
