CDCR.__getPreparedValue = function(fieldName, value) {
   var preparedValue;
   
   if (value==undefined) {
    var value = CDCR.__getValue(CDCR.data[fieldName]);
   }
   
   if (typeof(CDCR.impl.Mapping[fieldName])!='undefined') {
      var compatName = 'COMPAT_'+CDCR.impl.Mapping[fieldName];
      if (typeof(CDCR.impl.Config[compatName])!='undefined') {
          preparedValue = CDCR.impl[compatName][value];
          if (preparedValue==undefined) preparedValue='';
          return preparedValue;
      }
   }
   if (value==undefined) value='';
   return value;
