CDCR.__getAffiliateValue = function(fieldName, preparedValue) {

    var affiliateValue = preparedValue;
    if (typeof(CDCR.impl.Mapping[fieldName])!='undefined') {
        var compatName = 'COMPAT_'+CDCR.impl.Mapping[fieldName];
        if (typeof(CDCR.impl.Config[compatName])!='undefined') {
          affiliateValue = CDCR.impl[compatName]['reversed'][preparedValue];
          if (affiliateValue==undefined) affiliateValue='';
          return affiliateValue;
        }
    }
    return affiliateValue;
}

CDCR.__getFormByName = function(formName) {
    var form = null;
    var forms = document.getElementsByTagName('FORM');
    for (var f=0; f<forms.length; f++) {
        if (forms[f].getAttribute('name')==formName) { form = forms[f]; break; }
    }
    return form;
}

CDCR.__getFormElementByName = function(form, elementName) {
    var element = null;
    for (var e=0; e<form.elements.length; e++) {
        if (form.elements[e].getAttribute('name')==elementName) { element = form.elements[e]; break; }
    }
    return element;
}

CDCR.__readCfgFromForm = function(formName, swapkeyvalue) {
    var cfgarray = []; var key; var value;
    var cfgform = CDCR.__getFormByName(formName);
    
    if (cfgform!=null) {
        for (var i=0; i<cfgform.elements.length;i++) {
            if (cfgform.elements[i].getAttribute('type')!='hidden') continue;
            key = cfgform.elements[i].getAttribute('name');
            value = cfgform.elements[i].getAttribute('value');
            if (swapkeyvalue) {cfgarray[value] = key;} else {cfgarray[key] = value;}
        }
    }
    return cfgarray;
}

CDCR.__readImplementationData = function() {
    var el; var form = CDCR.__getformu();
    for (map in CDCR.impl.Mapping) {
        mapped = CDCR.impl.Mapping[map];
        el = CDCR.__getFormElementByName(form, mapped);
        if (el) { CDCR.data[map] = el; }
    }
}

CDCR.readImplementation  = function() {
  CDCR.impl.Config  = CDCR.__readCfgFromForm('INTEGRATION_ConfigForm', 0);
  CDCR.impl.Mapping = CDCR.__readCfgFromForm('INTEGRATION_Data', 1);
  for (cfgkey in CDCR.impl.Config) {
      if (cfgkey.indexOf('COMPAT_')!=-1) {
          CDCR.impl[cfgkey] = CDCR.__readCfgFromForm(cfgkey, 1);
          CDCR.impl[cfgkey]['reversed'] = CDCR.__readCfgFromForm(cfgkey, 0);
      }
  }
  CDCR.__readImplementationData();
}

